mapper.select:
SELECT totalCount, totalPage, currentPage,
CASE WHEN currentPage = 0 THEN 1 ELSE 0 END AS isFirst,
CASE WHEN currentPage = totalPage - 1 THEN 1 ELSE 0 END AS isLast

FROM
(SELECT COUNT(*) AS totalCount, CEIL(COUNT(*)/6) AS totalPage,
0 AS currentPage, 0 AS isFirst, 0 AS isLast

FROM jobBoard jB		
WHERE jB.userId = #{id}


		AND jB.jobAddress = #{keywordVO.jobplaceName}


		AND jB.jobCd = #{keywordVO.jobcdName}


) result

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY jobB.jobDate DESC) AS rno, jobB.jobNo, jobB.userId, jobB.jobCd, jobB.jobAddress, jobB.jobSub, jobB.jobText, CASE WHEN jobB.jobFin = 'Y' THEN '완료' ELSE '진행중' END AS jobFin
FROM jobBoard jobB WHERE jobB.userId = #{id} AND jobB.jobDelYn != 'Y'
AND jobB.jobAddress = #{keywordVO.jobplaceName}
AND jobB.jobCd = #{keywordVO.jobcdName}
) WHERE rno BETWEEN #{keywordVO.startNum} AND #{keywordVO.startNum} + 5 ORDER BY rno ASC

SELECT totalCount, totalPage, currentPage,
CASE WHEN currentPage = 0 THEN 1 ELSE 0 END AS isFirst,
CASE WHEN currentPage = totalPage - 1 THEN 1 ELSE 0 END AS isLast

FROM
(SELECT COUNT(*) AS totalCount, CEIL(COUNT(*)/12) AS totalPage,
0 AS currentPage, 0 AS isFirst, 0 AS isLast

FROM prLike prlik
LEFT OUTER JOIN prBoard prB
ON prB.prNo = prlik.prNo		
WHERE prlik.userId = #{id}


		AND prB.prCareer = #{keywordVO.prcareerName}


		AND prB.prMajor = #{keywordVO.prmajorName}


		AND prB.prLoc = #{keywordVO.prlocName}


		AND prB.prHash = #{keywordVO.prhashName}


) result

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY prB.prDate DESC) AS likeno, prlik.userId, prlik.prLikeNo, prlik.prNo, CASE WHEN prlik.prLike = 1 THEN '좋아요' ELSE '좋아요 취소' END AS prLikestat, prB.userId AS prwriterId, prB.prFile, prB.prSub, prB.prText, prB.prCareer, prB.prMajor
FROM prLike prlik LEFT OUTER JOIN prBoard prB ON prB.prNo = prlik.prNo
WHERE prlik.userId = #{id} AND prlik.prLike = 1
AND prB.prCareer = #{keywordVO.prcareerName}
AND prB.prMajor = #{keywordVO.prmajorName}
AND prB.prLoc = #{keywordVO.prlocName}
AND prB.prHash = #{keywordVO.prhashName}
) WHERE likeno BETWEEN #{keywordVO.startNum} AND #{keywordVO.startNum} + 11 ORDER BY likeno ASC

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY m.msgNo DESC) AS mNo, (SELECT COUNT(CASE WHEN m1.msgDelYn != 'Y' AND m1.sendSt = 1 THEN 1 ELSE NULL END) AS totalSndMsg FROM msg m1 WHERE m1.userId = #{id}) AS totalSndMsg, (SELECT COUNT(CASE WHEN m2.msgDelYn != 'Y' AND m2.readSt = 1 THEN 1 ELSE NULL END) AS totalRcvMsg FROM msg m2 WHERE m2.recvId = #{id}) AS totalRcvMsg, (SELECT COUNT(CASE WHEN m3.readSt = 1 AND m3.msgCheck = 0 THEN 1 ELSE NULL END) AS totalUncheckMsg FROM msg m3 WHERE m3.recvId = #{id} AND m3.msgDelYn != 'Y') AS totalUncheckMsg, m.msgNo AS msgNo, m.userId AS sendUserId, m.recvId AS rcvUserId, m.msgText AS msgContents, m.readSt AS isRcv, m.sendSt AS isSnd, m.msgCheck AS isCheck, m.msgDate AS msgDate, m.msgDelYn AS isMsgDel
FROM msg m WHERE m.msgDelYn != 'Y'
AND m.recvId = #{keywordVO.userRCV}
AND m.userId = #{keywordVO.userSND}
AND m.msgDate >= #{keywordVO.startDate}
AND m.msgDate <= #{keywordVO.endDate}
AND m.msgText LIKE '%' || #{keywordVO.keyInTxt} || '%'
) WHERE mNo BETWEEN #{keywordVO.startNum} AND #{keywordVO.startNum} + 7 ORDER BY msgNo DESC

SELECT totalCount, totalPage, currentPage,
CASE WHEN currentPage = 0 THEN 1 ELSE 0 END AS isFirst,
CASE WHEN currentPage = totalPage - 1 THEN 1 ELSE 0 END AS isLast

FROM
(SELECT COUNT(*) AS totalCount, CEIL(COUNT(*)/8) AS totalPage,
0 AS currentPage, 0 AS isFirst, 0 AS isLast

FROM msg ms		
WHERE ms.msgDelYn != 'Y'


		AND ms.recvId = #{keywordVO.userRCV}


		AND ms.userId = #{keywordVO.userSND}


		AND ms.msgDate >= #{keywordVO.startDate}


		AND ms.msgDate <= #{keywordVO.endDate}


		AND ms.msgText LIKE '%' || #{keywordVO.keyInTxt} || '%'


) result

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY qa.qnaDate DESC) AS qNo, qa.qnaNo AS qnaNo, qa.qnaSub AS qnaSub, qa.qnaText AS qnaText, qa.qnaFile AS qnaFile, qa.qnaDate AS qnaDate, qa.qnaUpdate AS qnaUpdate, qa.qnaDelYn AS qnaDelYn
FROM qnaBoard qa
WHERE qa.userId = #{id}
AND ( qa.qnaSub LIKE '%' || #{keyword} || '%' OR qa.qnaText LIKE '%' || #{keyword} || '%' )
)ORDER BY qNo ASC
